import Vue from 'vue'
import App from './App.vue'
import './registerServiceWorker'
import router from './router'
import store from './store'
import axios from 'axios'
import ElementUI from 'element-ui';
import 'element-ui/lib/theme-chalk/index.css';


Vue.config.productionTip = false

new Vue({
  router,
  store,
  render: h => h(App)
}).$mount('#app')
Vue.prototype.$axios = axios
Vue.use(ElementUI);
axios.defaults.baseURL = 'http://127.0.0.1:8000';
instance.defaults.withCredentials=true

instance.interceptors.request.use(
    (config) => {
        // const username = localStorage.getItem('username');//改成cookie 不懂cookie，，，，，
        // const authorization = localStorage.getItem('token');//改成cookie 不懂cookie，，，，，
        const username = cookie.getCookie('username')
        const authorization = cookie.getCookie('token')
        // 若 localStorage 中含有这些字段，则添加入请求头
        //config.data = qs.stringify(config.data)
        if (username && authorization) {
            console.log(authorization)
            config.headers.authorization = authorization;
            config.headers.username = username;
        }
        return config;
    },
    (error) => {
        return Promise.reject(error)
    }
)
export default instance
